import Input from '../Input';
import styled from 'styled-components';
import { useState } from 'react';
import { livros } from './dadosPesquisa';

const PesquisaContainer = styled.section`
    color: #FFF;
    text-align: center;
    padding: 100px 0; /* Aumente a altura para dar mais espaço */
    width: 100%;
    font-family: 'Arial', sans-serif; /* Adicione uma fonte mais atraente */
`;

const Titulo = styled.h2`
    color: #FFF;
    font-size: 36px;
    text-align: center;
    width: 100%;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5); /* Adicione uma sombra de texto */
`;

const Subtitulo = styled.h3`
    font-size: 16px;
    font-weight: 500;
    margin-bottom: 60px; /* Aumente a margem inferior para separar melhor do conteúdo abaixo */
`;

const Resultado = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    margin-bottom: 20px;
    padding: 15px; /* Adicione um espaçamento interno */
    cursor: pointer;
    transition: background-color 0.3s ease; /* Adicione uma transição suave */
    
    &:hover {
        background-color: rgba(255, 255, 255, 0.1); /* Altere a cor de fundo no hover */
    }

    p {
        font-size: 18px; /* Aumente o tamanho da fonte */
        margin-left: 15px; /* Adicione um espaçamento à esquerda */
        color: #FFF;
    }

    img {
        width: 100px;
        border: 2px solid #FFF;
        border-radius: 5px;
    }
`;

function Pesquisa() {
    const [livrosPesquisados, setLivrosPesquisados] = useState([]);

    return (
        <PesquisaContainer>
            <Titulo>Já sabe por onde começar?</Titulo>
            <Subtitulo>Encontre seu livro em nossa estante.</Subtitulo>
            <Input
                placeholder="Escreva sua próxima leitura"
                onBlur={(evento) => {
                    const textoDigitado = evento.target.value;
                    const resultadoPesquisa = livros.filter((livro) => livro.nome.includes(textoDigitado));
                    setLivrosPesquisados(resultadoPesquisa);
                }}
            />
            {livrosPesquisados.map((livro) => (
                <Resultado key={livro.id}>
                    <img src={livro.src} alt={livro.nome} />
                    <p>{livro.nome}</p>
                </Resultado>
            ))}
        </PesquisaContainer>
    );
}

export default Pesquisa;
